import{_ as e,c as t,o,R as a}from"./chunks/framework.27a1c15b.js";const g=JSON.parse('{"title":"Roles and Permissions","description":"","frontmatter":{},"headers":[],"relativePath":"guide/roles.md","lastUpdated":1685161690000}'),s={name:"guide/roles.md"},r=a('<h1 id="roles-and-permissions" tabindex="-1">Roles and Permissions <a class="header-anchor" href="#roles-and-permissions" aria-label="Permalink to &quot;Roles and Permissions&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>By default, one organisation can have <strong>maximum 10 accounts</strong> (of any combination of roles)</p></div><h2 id="superadmin" tabindex="-1">Superadmin <a class="header-anchor" href="#superadmin" aria-label="Permalink to &quot;Superadmin&quot;">​</a></h2><ul><li>There is <strong>only one</strong> superadmin per organisation</li><li>It <strong>CANNOT be deleted</strong> though it can be transferred</li><li>It has the <strong>full access permissions</strong> to organisation and its assets and accounts</li></ul><h2 id="admin" tabindex="-1">Admin <a class="header-anchor" href="#admin" aria-label="Permalink to &quot;Admin&quot;">​</a></h2><ul><li>It has full access permissions to organisation&#39;s assets and accounts, <strong>except for <code>superadmin</code></strong> account.</li><li>Only administrators (incl. <code>superadmin</code>) <strong>can create organisations</strong>.</li><li>Administrators can <strong>transfer assets</strong> between organisations (so long as the account takes admin roles in both organisations)</li></ul><h2 id="operator" tabindex="-1">Operator <a class="header-anchor" href="#operator" aria-label="Permalink to &quot;Operator&quot;">​</a></h2><ul><li>It has full <strong>operational permissions</strong> to organisation&#39;s assets</li><li>It <strong>CANNOT transfer assets</strong></li></ul><h2 id="user" tabindex="-1">User <a class="header-anchor" href="#user" aria-label="Permalink to &quot;User&quot;">​</a></h2><ul><li>Read-only permissions</li></ul><h2 id="operational-permissions" tabindex="-1">Operational Permissions <a class="header-anchor" href="#operational-permissions" aria-label="Permalink to &quot;Operational Permissions&quot;">​</a></h2><table><thead><tr><th>Role</th><th>Resources</th><th>Actions</th></tr></thead><tbody><tr><td>User</td><td><code>SIM</code></td><td><code>read</code>, <code>export</code></td></tr><tr><td>User</td><td><code>SMS</code></td><td><code>read</code></td></tr><tr><td>User</td><td><code>CDR</code></td><td><code>read</code></td></tr><tr><td>Operator</td><td><code>SIM</code></td><td><code>read</code>,<code>export</code>, <code>suspend</code>, <code>resume</code>, <code>reset</code>, <code>tag</code>,<code>note</code></td></tr><tr><td>Operator</td><td><code>SMS</code></td><td><code>read</code>, <code>send</code></td></tr><tr><td>Operator</td><td><code>CDR</code></td><td><code>read</code></td></tr></tbody></table><h2 id="account-management" tabindex="-1">Account Management <a class="header-anchor" href="#account-management" aria-label="Permalink to &quot;Account Management&quot;">​</a></h2><p>Administrators can <strong>create, delete, enable/disable, edit accounts</strong> from <code>settings</code> page.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p><a href="/account/">Account Management - Deep Dive</a></p></div>',15),d=[r];function n(i,c,l,u,m,h){return o(),t("div",null,d)}const b=e(s,[["render",n]]);export{g as __pageData,b as default};
